class Solution {
public:
    vector<int> maxSlidingWindow(vector<int>& nums, int k) {
        vector<int> res;
        int i = 0;
        while (i < nums.size()) {
            int j = i;
            int max = nums[i];
            while (j < nums.size() && j - i < k) {
                if (max < nums[j]) {
                    max = nums[j];
                }
                j++;
            }
            res.push_back(max);
            i = j;
        }
        return res;
    }
};

