#include <stdio.h>
#include<vector>
#include<algorithm>
usingnamespace std;

struct Node{
    int data;
    Node *left;
    Node *right;
};

Node* build(vector<int> &v, int start, int end){
    if(start > end){
        return NULL;
    }
    int mid = (start + end) / 2;
    Node *node = new Node();
    node->data = v[mid];
    node->left = build(v, start, mid - 1);
    node->right = build(v, mid + 1, end);
    return node;
}

void print(Node *node){
    if(node == NULL){
        return;
    }
    print(node->left);
    printf("%d ", node->data);
    print(node->right);
}

int main(){
    int n;
    scanf("%d", &n);
    vector<int> v(n);
    for(int i = 0; i < n; i++){
        scanf("%d", &v[i]);
    }
    Node *root = build(v, 0, n - 1);
    print(root);
    return 0;
}
