smallest_change({1,2,3,5,4,7,9,6}) == 4
smallest_change({1, 2, 3, 4, 3, 2, 2}) == 1
smallest_change({1, 2, 3, 2, 1}) == 0
*/
#include<stdio.h>
#include<vector>
using namespace std;
int smallest_change(vector<int> arr){
    int n = arr.size();
    int i = 0;
    int j = n-1;
    int min_change = 0;
    while(i<j){
        if(arr[i]!= arr[j]){
            min_change++;
            i++;
            j--;
        }
        else{
            i++;
            j--;
        }
    }
    return min_change;
}
