				temp.push_back(lst1[i]);
				lst1.erase(lst1.begin()+i);
				lst2.erase(lst2.begin()+j);
				break;
			}
		}
	}
	if(lst1.size()==0){
		return "YES";
	}
	else{
		return "NO";
	}
}
