Note:
Rounding away from zero means that if the given number is equidistant
from two integers, the one you should return is the one that is the
farthest from zero. For example closest_integer("14.5") should
return 15 and closest_integer("-14.5") should return -15.
*/
#include<stdio.h>
#include<math.h>
#include<string>
using namespace std;
int closest_integer(string value){
    int num = stoi(value);
    int num_int = floor(num);
    int num_int_plus_one = ceil(num);
    if(num_int == num_int_plus_one){
        return num_int;
    }
    else{
        if(abs(num_int - num_int_plus_one) < abs(num - num_int)){
            return num_int_plus_one;
        }
        else{
            return num_int;
        }
    }
}
int main(){
    string value;
    cout<<"Enter a number: ";
    cin>>value;
    cout<<"The closest integer to "<<value<<" is "<<closest_integer(value)<<endl;
    return 0;
}
