     */
    public List<Integer> uniqueDigits(List<Integer> x) {
        List<Integer> result = new ArrayList<Integer>();
        if (x.size() == 0) return result;
        int last = x.get(x.size() - 1);
        for (int i = 0; i < last; i++) {
            if (x.size() == 1) {
                result.add(last);
                break;
            }
            int n = last;
            while (n > 0) {
                if (n % 10 == 0) {
                    result.add(n);
                    break;
                }
                n /= 10;
            }
        }
        return result;
    }
}

