class Solution {
    /**
    Return lis t of  all p r efixes  from sho rtest to longest of t he input string 
    >>> allPrefixes("abc")
    ["a", "ab", "abc"]
     */
    public List<String> allPrefixes(String string) {
        List<String> result = new ArrayList<String>();
        if(string == null || string.length() == 0) return result;

        int[] count = new int[string.length()];
        Arrays.fill(count, 0);

        for(int i = 0; i < string.length(); i++) {
            for(int j = i; j >= 0; j--) {
                if(count[j] == 0) {
                    result.add(string.substring(j, i + 1));
                    count[j]++;
                    break;
                }
            }
        }
        return result;
    }
}

