    public List<String> allPrefixes(String string) {
        List<String> result = new ArrayList<String>();
        Switch(string, 0, result);
        return result;
    }

    public void Switch(String string, int index, List<String> result) {
        if(index == string.length()) {
            result.add(string);
            return;
        }

        for(int i = index; i < string.length(); i++) {
            String sub = string.substring(index, i + 1);
            if(isPrefix(sub)) {
                Switch(string, i + 1, result);
            }
        }
    }

    public boolean isPrefix(String string) {
        for(int i = 0; i < string.length(); i++) {
            if(string.charAt(i)!= string.charAt(i + 1)) {
