//   selectWords("Uncle sam", 3) ==> ["Uncle"]
// 
const selectWords = (s, n) => {
  let t = s.split(/\s/)
  if (s == '') { return [] }
  let k = []
  for (let i = 0; i < t.length; i++) {
    let l = 0
    for (let j = 0; j < t[i].length; j++) {
      if (t[i][j]!= 'a' && t[i][j]!= 'e' && t[i][j]!= 'i' && t[i][j]!= 'o' && t[i][j]!= 'u' && t[i][j]!= 'A' &&
        t[i][j]!= 'E' && t[i][j]!= 'I' && t[i][j]!= 'O' && t[i][j]!= 'U') {
        l++
      }
    }
    if (l == n) { k.push(t[i]) }
  }
  return k
}

