const findDuplicate = (n) => {
  let arr = []
  for (let i = 0; i < n; i++) {
    arr.push(i)
  }
  let result = 0
  let i = 0
  let j = 0
  while (i < n) {
    if (arr[i] == arr[j]) {
      result = arr[i]
      i++
      j++
    } else {
      if (j > i) {
        arr[j] = arr[i]
        j++
      }
      i++
    }
  }
  return result
}

