	var evens = arr.filter(x => x % 2 == 0);
	var min = evens[0];
	var min_index = evens.indexOf(min);
	var min_even = evens.find(x => x == min);
	var min_even_index = evens.indexOf(min_even);
	var result = [min, min_index];
	if (min_index!= min_even_index) {
		result = [min_even, min_even_index];
	}
	return result;
};

