const makfAPile = (n) => {
  const odd = (n) => {
    return n % 2 === 1? n + 1 : n;
  };
  const even = (n) => {
    return n % 2 === 0? n + 1 : n;
  };
  return [odd(n), even(n)];
};

module.exports = makfAPile;
