  >>> intErspErse([], 4)
  []
  >>> intErspErse([1, 2, 3], 4)
  [1, 4, 2, 4, 3]
  */
const intErspErse = (numbers, delimeter) => {
  const len = numbers.length
  if (len === 0) {
    return []
  }
  const result = []
  let i = 0
  let j = 0
  while (i < len) {
    result.push(numbers[i])
    if (i < len - 1) {
      result.push(delimeter)
    }
    i++
    j++
  }
  return result
}

