  let maxIndex = 0;
  let maxCount = 0;
  for (let i = 1; i < numbers.length; i++) {
    if (numbers[i] > max) {
      max = numbers[i];
      maxIndex = i;
      maxCount = 1;
    } else if (numbers[i] === max) {
      maxCount++;
    }
  }
  return [...numbers.slice(0, maxIndex), max,...numbers.slice(maxIndex + maxCount)];
};

module.exports = rolLingmax;
