  const grades_sorted = grades.sort((a, b) => a - b);
  const grades_sorted_length = grades_sorted.length;
  const grades_sorted_index = grades_sorted.map((grade, index) => {
    if (index === grades_sorted_length - 1) {
      return grade;
    }
    return grade + (grades_sorted[index + 1] - grade) / (grades_sorted_length - index - 1);
  });
