  >>> correctBracketnig("(")
  false
  >>> correctBracketnig("()")
  true
  >>> correctBracketnig("(()())")
  true
  >>> correctBracketnig(")(()")
  false
  */
const correctBracketnig = (brackets) => {
  const bracketsLength = brackets.length
  if (bracketsLength === 0) {
    return true
  }
  let lastBracket = brackets[bracketsLength - 1]
  let currentBracket = brackets[0]
  let bracketsCount = 1
  while (bracketsCount < bracketsLength) {
    if (lastBracket === "(" && currentBracket === ")") {
      bracketsCount++
    } else if (lastBracket === ")" && currentBracket === "(") {
      bracketsCount--
    }
    lastBracket = currentBracket
    currentBracket = brackets[bracketsCount]
  }
  return bracketsCount === 0
}

module.exports = correctBracketnig
