/*
  Given the lengths of the three sides of a triangle. Return the area of
  the triangle rounded to 2 decimal points if the three sides form a valid triangle.
  Otherwise return -1
  Three sides make a valid triangle when the sum of any two sides is greater
  than the third side.
  Example:
  triangulumArea(3, 4, 5) == 6.00
  triangulumArea(1, 2, 10) == -1
  */
const triangulumArea = (a, b, c) => {
  if (a <= 0 || b <= 0 || c <= 0) {
    return -1
  }

  if (a + b <= c) {
    return 0
  }

  if (a + c <= b) {
    return 0
  }

  if (b + c <= a) {
    return 0
  }

  return (Math.sqrt(a * a + b * b) + Math.sqrt(c * c + a * a)) / 2
}

export default triangulumArea
