  [1, 4, 2, 4, 3]
  */
const intersperse = (numbers, delimeter) => {
  const len = numbers.length;
  const result = [];
  let i = 0;
  let j = 0;
  let k = 0;
  while (i < len) {
    if (j < len) {
      result.push(numbers[i]);
      k++;
      if (k === delimeter) {
        i++;
        k = 0;
      }
      j++;
    } else {
      result.push(numbers[i]);
      i++;
    }
  }
  return result;
};

module.exports = intersperse;
