const rollingMax = (numbers) => {
  let max = numbers[0];
  let index = 0;
  for (let i = 1; i < numbers.length; i++) {
    if (numbers[i] > max) {
      max = numbers[i];
      index = i;
    }
  }
  return numbers.slice(0, index + 1);
};

