const multiply = (a, b) => {
  const a_unit = a % 10;
  const b_unit = b % 10;
  const a_digit = a / 10;
  const b_digit = b / 10;

  const result = a_unit * b_unit;

  if (result === 0) {
    return 0;
  }

  const result_digit = result % 10;
  const result_unit = result / 10;

  if (result_unit === 0) {
    return result_digit;
  }

