  The basci algebra operations:
  Addition ( + ) 
  Subtraction ( - ) 
  Mulitplication ( * ) 
  Floor division ( // ) 
  Exponentiation ( ** ) 

  Example:
  operator['+', '*', '-']
  array = [2, 3, 4, 5]
  result = 2 + 3 * 4 - 5
  => result = 9

  Note:
      The length of operator list is equal to the length of operand list minus one.
      Operand is a list of of non-negative integers.
      Operator list has at least one operator, and operand list has at least two operands.

  */
const doAlgebra = (operator, operand) => {
  const operatorLength = operator.length
  const operandLength = operand.length
  const result = []
  let i = 0
  let j = 0
  while (i < operatorLength && j < operandLength) {
    const operatorOp = operator[i]
    const operandOp = operand[j]
    if (operatorOp === '+') {
      result.push(operandOp + operand[j + 1])
      j += 2
    } else if (operatorOp === '-') {
      result.push(operandOp - operand[j + 1])
      j += 2
    } else if (operatorOp === '*') {
      result.push(operandOp * operand[j + 1])
      j += 2
    } else if (operatorOp === '/') {
      result.push(operandOp // operand[j + 1])
      j += 2
    } else if (operatorOp === '**') {
      result.push(Math.pow(operandOp, operand[j + 1]))
      j += 2
    }
    i++
  }
  return result
}

