  multiply(14,-15) should return 20.
  */
const multiply = (a, b) => {
  const aDigits = a.toString().split('').reverse().join('');
  const bDigits = b.toString().split('').reverse().join('');
  const aDigitsLength = aDigits.length;
  const bDigitsLength = bDigits.length;
  let product = 0;
  for (let i = 0; i < aDigitsLength; i++) {
    for (let j = 0; j < bDigitsLength; j++) {
      product += parseInt(aDigits[i] * bDigits[j]);
    }
  }
  return product;
};

