const fruitDistribution = (s, n) => {
  let count = 0
  let map = 0
  let total = 0
  let i = 0
  while (i < s.length) {
    let c = s[i]
    if (c === 'apple') {
      count += 1
    } else if (c === 'orange') {
      count += 1
      map += 1
    } else if (c ==='mango') {
      count += 1
      map += 1
    }
    total += count
    i += 1
  }
  return total - map
}

