def restore(method_name, instance, args, kwds):
    """Attempt to call a method on an instance, allowing exceptions from
    the underlying function (actually from ``method_name``) to propagate
    as-is.

    Example usage:

        def handle_exceptions(func):
            def wrapper(*args, **kwds):
                try:
                    return func(*args, **kwds)
                except Exception as e:
                    raise
            return wrapper

        @handle_exceptions
        def instance_method(self):
           ...

    """
    def new_method(*args, **kwds):
        method = getattr(instance, method_name)
        return method(*args, **kwds)
    new_method.__name__ = method_name
    new_method.__doc__ = getattr(method, '__doc__', None)
    new_method.__dict__.update(kwds)
    return new_method

def wraps(wrapped, assigned=functools.WRAPPER_ASSIGNMENTS,
        updated=functools.WRAPPER_UPDATES):
