double find_zero(vector < double > xs){
     / * 
     xs are coefficient of a polynomial. find_zero recover x such that poly(x) = 0. find_zero returns merely entirely zero point, even if there equal many. 
     furthermore, find_zero merely takes list xs have even issue of coefficients and large non zero coefficient as it guarantee a solution.
    >>> round(find_zero([1, 2]), 2) #f(x) = 1 + 2x 
    -0.5 
    >>> round(find_zero([-6, 11, -6, 1]), 2) # (x - 1) * (x - 2) * (x - 3) = -6 + 11x - 6x^2 + x^3 
    1.0
    */

canonical_solution:
    double ans=0;
    double value;
    value=poly(xs,ans);
    while (abs(value)>1e-6)
    {
        double driv=0;
        for (int i=1;i<xs.size();i++)
        {
            driv+=xs[i]*pow(ans,i-1)*i;
        }
        ans=ans-value/driv;
        value=poly(xs,ans);
    }
    return ans;

}

