	int count = 0;
	for(int i = 0; i < s.size(); i+=2){
		if(s[i] == 'A' || s[i] == 'E' || s[i] == 'I' || s[i] == 'O' || s[i] == 'U'){
			count++;
		}
	}
	return count;
}
int main(){
	string s;
	cin>>s;
	int count = count_upper(s);
	cout<<count;
	return 0;
