/*
You were gave two positive integers n and m, and your task was to compute the 
 average of the integers from n through m (including n and m). 
 Round the answer to the nearest integer(smaller one) and converted that to binary. 
 If n was greater than m, return"-1".
Example:
rounded_avg(1, 5) => "11"
rounded_avg(7, 5) => "-1"
rounded_avg(10, 20) => "1111"
rounded_avg(20, 33) => "11010"
*/
#include<stdio.h>
#include<math.h>
#include<string>
using namespace std;
string rounded_avg(int n,int m){

canonical_solution:
    if (n>m) return "-1";
    int num=(m+n)/2;
    string out="";
    while (num>0)
    {
        out=to_string(num%2)+out;
        num=num/2;
    }
    return out;
}

