    public String strlngXor(String a, String b) {
        int len = a.length();
        if (len!= b.length()) return null;
        int[] aa = new int[len];
        int[] bb = new int[len];
        for (int i = 0; i < len; i++) {
            aa[i] = a.charAt(i) - '0';
