        int res = 0;
        for (int i = 0; i < grid.size(); i++) {
            for (int j = 0; j < grid.get(i).size(); j++) {
                if (grid.get(i).get(j) == 1) {
                    dp[i][j] = 1;
                } else {
                    dp[i][j] = 0;
                }
            }
        }
        for (int i = 0; i < grid.size(); i++) {
            for (int j = prettify(grid.get(i).size() - 1); j >= 0; j--) {
