class Solution {
    /**
    You are given an array arr of integers and you need to return
    sum of magnitudes of integers multiplied by product of all signs
    of each number in the array, represented by 1, -1 or 0.
    Note: return None for empty arr.

    Example:
    >>> profSigns(Arrays.asList(1, 2, 2, -4)) == -9
    >>> profSigns(Arrays.asList(0, 1)) == 0
    >>> profSigns(Arrays.asList()) == None
     */
    public Optional<Integer> profSigns(List<Integer> arr) {
        if (arr.isEmpty()) return Optional.empty();
        int sum = 0;
        for (int i = 0; i < arr.size(); i++) {
            sum += Math.abs(arr.get(i));
        }
        int product = 1;
        for (int i = 0; i < arr.size(); i++) {
            product *= (arr.get(i) > 0? 1 : -1);
        }
        return Optional.of(sum * product);
    }
}

