        for (int i = 0; i < string.length(); i++) {
            char c = string.charAt(i);
            if (c == '[') {
                stack.push(c);
            } else if (c == ']') {
                if (stack.isEmpty()) {
                    return false;
                }
                char top = stack.pop();
                if (top == '[') {
                    stack.push(c);
                }
            }
        }
        return stack.isEmpty();
    }
