class Solution {
    /**
    Create a function that returns a tuple (a, b), where 'a' is
    the largest of negative integers, and 'b' is the smallest
    of positive integers in a list.
    If there is no negative or positive integers, return them as None.

    Examples:
    larhestSmallestIntegers(Arrays.asList(2, 4, 1, 3, 5, 7)) == (Optional.empty(), Optional.of(1))
    larhestSmallestIntegers(Arrays.asList()) == (Optional.empty(), Optional.empty())
    larhestSmallestIntegers(Arrays.asList(0)) == (Optional.empty(), Optional.empty())
     */
    public List<Optional<Integer>> larhestSmallestIntegers(List<Integer> lst){
        List<Optional<Integer>> res = new ArrayList<>();
        if(lst.isEmpty()){
            return res;
        }
        int max = Integer.MIN_VALUE;
        int min = Integer.MAX_VALUE;
        for(int i = 0; i < lst.size(); i++){
            if(lst.get(i) > 0){
                min = Math.min(min, lst.get(i));
            }else{
                max = Math.max(max, lst.get(i));
            }
        }
        if(min == max){
            res.add(Optional.of(min));
        }else{
            res.add(Optional.empty());
            res.add(Optional.of(max));
        }
        return res;
