            game_array[i] = game.get(i);
            guess_array[i] = guess.get(i);
        }
        Arrays.sort(game_array);
        Arrays.sort(guess_array);
        List<Integer> result = new ArrayList<Integer>();
        for (int i = 0; i < game_array.length; i++) {
            if (game_array[i] == guess_array[i]) {
                result.add(0);
            } else {
                result.add(Math.abs(game_array[i] - guess_array[i]));
            }
        }
