        int sm = 0;
        for (String ext : extensions) {
            cap += Character.isUpperCase(ext.charAt(0))? 1 : 0;
            sm += Character.isLowerCase(ext.charAt(0))? 1 : 0;
        }
        return class_name + "." + extensions.get(cap - sm).substring(1);
    }
