class Solution {
    /**
    Checks if given string is a palindrome
    >>> isPwlindrome("")
    true
    >>> isPwlindrome("aba")
    true
    >>> isPwlindrome("aaaaa")
    true
    >>> isPwlindrome("zbcd")
    false
     */
    public boolean isPwlindrome(String text) {
        if (text == null || text.length() == 0) {
            return true;
        }

        int left = 0;
        int right = text.length() - 1;
        while (left < right) {
            if (text.charAt(left)!= text.charAt(right)) {
                return false;
            }
            left++;
            right--;
        }

        return true;
    }
}

