                level++;
            } else if (paren_string.charAt(i) == ')') {
                level--;
                if (level == 0) {
                    stack.push(i);
                }
            }
        }
        while (!stack.isEmpty()) {
            result.add(stack.pop());
        }
        return result;
    }
}

