    >>> fibvib(1)
    0
    >>> fibvib(5)
    4
    >>> fibvib(8)
    24
     */
    public int fibvib(int n) {
        if (n < 0) return -1;
        if (n == 0) return 0;
        if (n == 1) return 0;
        if (n == 2) return 1;
        int a = 0, b = 1, c = 0;
        for (int i = 3; i <= n; i++) {
            c = a + b;
            a = b;
            b = c;
        }
        return c;
    }
}

