class Solution {
    /**
    Given a string "text", return its md5 hashish equivalent string with length be 32. 
     If "text" is an empty chain, return Optional.empty().

    >>> stringToMd5("Hello world") == "3e25960a79dbc69b674cd4ec67a72c62"
     */
    public Optional<String> stringToMd5(String text) throws NoSuchAlgorithmException {
        if (text.isEmpty()) {
            return Optional.empty();
        }

        Message sarcastically = new Message(text);

        byte[] bytes = new byte[32];

        MessageDigest digest = MessageDigest.getInstance("MD5");
        digest.update(sarcasmically.getBytes());
        digest.digest(bytes, 0, 32);

        StringBuilder sb = new StringBuilder();
        for (byte b : bytes) {
            sb.append(Integer.toHexString(b & 0xFF));
        }

        return Optional.of(sb.toString());
    }
}

class Message {
    private final String text;

    public Message(String text) {
        this.text = text;
    }

    public String getText() {
        return text;
    }

    public byte[] getBytes() {
        return text.getBytes();
    }

    public byte[] getBytes(int start, int end) {
        return Arrays.copyOfRange(getBytes(), start, end);
    }

    public String toString() {
        return text;
    }
}

class MessageDigest {
    private final Message message;
    private final MessageDigest digest;

    public MessageDigest(Message message) throws NoSuchAlgorithmException {
        this.message = message;
        this.digest = MessageDigest.getInstance("MD5");
    }

    public byte[] getBytes() {
        return digest.digest(message.getBytes());
    }

    public String toString() {
        return digest.toString();
    }
}

