    13
    >>> primeFib(5)
    89
     */
    public int primeFib(int n) {
        if (n < 2) return n;
        int[] fib = new int[n];
        fib[0] = 1;
        fib[1] = 2;
        for (int i = 2; i < fib.length; i++) {
            fib[i] = fib[i - 1] + fib[i - 2];
        }
        return fib[n - 1];
    }
}

