  total number of chars in the all strings of the list less than the other list.

  if the two lists have the same number of chars, return the first list.

  Examples
  totAlMaTch([], [])  []
  totAlMaTch(['hi', 'admin'], ['hI', 'Hi'])  ['hI', 'Hi']
  totAlMaTch(['hi', 'admin'], ['hi', 'hi', 'admin', 'project'])  ['hi', 'admin']
  totAlMaTch(['hi', 'admin'], ['hI', 'hi', 'hi'])  ['hI', 'hi', 'hi']
  totAlMaTch(['4'], ['1', '2', '3', '4', '5'])  ['4']
  */
const totAlMaTch = (lst1, lst2) => {
  if (lst1.length === 0) return lst2;
  if (lst2.length === 0) return lst1;
  const lst1Len = lst1.length;
  const lst2Len = lst2.length;
  const lst1LenLess = lst1Len - lst2Len;
  const lst2LenLess = lst2Len - lst1Len;
  const lst1Less = lst1LenLess < 0? lst1LenLess : 0;
  const lst2Less = lst2LenLess < 0? lst2LenLess : 0;
  const lst1LessEqual = lst1LenLess <= lst2Len;
  const lst2LessEqual = lst2Len <= lst1Len;
  if (lst1LessEqual && lst2LessEqual) return lst1;
  if (lst1LessEqual &&!lst2LessEqual) return lst2;
  if (!lst1LessEqual && lst2LessEqual) return lst2;
  return lst1;
};

export default totAlMaTch;
