  isHappiest(aa) => false
  isHappiest(abcd) => true
  isHappiest(aabb) => false
  isHappiest(adb) => true
  isHappiest(xyy) => false
  */
const isHappiest = (s) => {
  if (s.length < 3) return false;
  const map = new Map();
  for (let i = 0; i < s.length - 2; i++) {
    const [a, b] = [s[i], s[i + 1]];
    if (map.has(a) && map.get(a) === b) return false;
    map.set(a, b);
  }
