const intersperse = (numbers, delimeter) => {
  let result = [];
  let i = 0;
  let j = 0;
  let k = 0;
  while (i < numbers.length) {
    if (j < numbers.length) {
      result.push(numbers[i]);
      i++;
      j++;
      k = 0;
    } else {
      result.push(numbers[i]);
      i++;
      k++;
      if (k < delimeter) {
        result.push(delimeter);
        k++;
      }
    }
  }
  return result;
};

module.exports = intersperse;
