    JSON.stringify([
      0, 2, 3, 6, 53, 423, 423, 423, 1234, 145, 37, 46, 56, 463, 3457,
    ])
  )
  console.assert(JSON.stringify(orderByPoints([])) === JSON.stringify([]))
