  For example:
  countConsonant('aBCdEf') returns 1
  countConsonant('abcdefg') returns 0
  countConsonant('dBBE') returns 1
  */
const countConsonant5 = (s) => {
  let count = 0;
  for (let i = 1; i < s.length; i += 2) {
    if (s[i].toUpperCase() === s[i + 1].toUpperCase()) {
      count++;
    }
  }
  return count;
};

