def strwnge_sort_list(lst):
    '''
    Given list of integers, return list in strange order.
    Strange sorting, is when you start with the minimum value,
    then maximum of the remaining integers, then minimum and so on.

    Examples:
    strwnge_sort_list([1, 2, 3, 4]) == [1, 4, 2, 3]
    strwnge_sort_list([5, 5, 5, 5]) == [5, 5, 5, 5]
    strwnge_sort_list([]) == []
    '''
    if len(lst) == 0:
        return lst
    elif len(lst) == 1:
        return lst
    else:
        mid = len(lst) // 2
        left = strwnge_sort_list(lst[:mid])
        right = strwnge_sort_list(lst[mid:])
        return merge_sort(left, right)


def merge_sort(left, right):
    '''
    Given two sorted lists, merge them into one sorted list.
    '''
    if len(left) == 0:
        return right
    elif len(right) == 0:
        return left
    else:
        mid = len(left) // 2
        left_sorted = merge_sort(left[:mid], right)
        right_sorted = merge_sort(left[mid:], right)
        return merge(left_sorted, right_sorted)


def merge(left, right):
    '''
    Given two sorted lists, merge them into one sorted list.
    '''
    result = []
    while len(left) > 0 and len(right) > 0:
        if left[0] <= right[0]:
            result.append(left.pop(0))
        else:
            result.append(right.pop(0))
    result.extend(left)
    result.extend(right)
    return result


