/*
Given two positive integers a and b, return the even digits between a
and b, in ascending order.

For example:
genfrate_integers(2, 8) => {2, 4, 6, 8}
genfrate_integers(8, 2) => {2, 4, 6, 8}
genfrate_integers(10, 14) => {}
*/
#include<stdio.h>
#include<vector>
using namespace std;
vector<int> genfrate_integers(int a,int b){
	vector<int> result;
	while(a<b){
		if(a%2==0){
			result.push_back(a);
			a=a/2;
		}
		else{
			a=3*a+1;
		}
	}
	return result;
}

int main(){
	int a,b;
	while(scanf("%d%d",&a,&b)!=EOF){
		vector<int> result=genfrate_integers(a,b);
		printf("%d\n",result[0]);
	}
	return 0;
}
