*/
#include<stdio.h>
#include<math.h>
#include<vector>
#include<string>
using namespace std;
#include<algorithm>
#include<stdlib.h>
int do_algerba(vector<string> operato, vector<int> operand){
    int res = 0;
    for(int i = 0; i < operato.size(); i++){
        if(operato[i] == "+"){
            res += operand[i];
        }else if(operato[i] == "-"){
            res -= operand[i];
        }else if(operato[i] == "*"){
            res *= operand[i];
        }else if(operato[i] == "//"){
            res /= operand[i];
        }else if(operato[i] == "**"){
            res = (int)pow(operand[i], 2);
        }
    }
    return res;
}
