            operato.push_back(s);
        }
        for(int i = 0; i < n; i++){
            int a;
            scanf("%d", &a);
            operand.push_back(a);
        }
        int result = do_algerba(operato, operand);
        printf("%d\n", result);
    }
    return 0;
}
