and b, in ascending order.

For example:
generate_integers(2, 8) => {2, 4, 6, 8}
generate_integers(8, 2) => {2, 4, 6, 8}
generate_integers(10, 14) => {}
*/
#include<stdio.h>
#include<vector>
using namespace std;
vector<int> generate_integers(int a,int b){
	vector<int> result;
	for(int i=a;i<=b;i++){
		int x = i;
		int temp = x;
		if(x%2!=0){
			while(x%2==0){
				x/=2;
			}
			temp = x;
		}
		result.push_back(temp);
	}
	return result;
}

int main(){
	vector<int> result;
	int a,b;
	while(scanf("%d %d",&a,&b)!=EOF){
		result = generate_integers(a,b);
		for(auto i:result){
			printf("%d ",i);
		}
		printf("\n");
	}
	return 0;
}
