#include<vector>
#include<string>
#include<algorithm>
usingnamespace std;
vector < string > sorted_list_sum(vector < string > lst) {
    vector < string > n66 = {
        }
    ;
    for (int i = 0; i < lst.size(); i++)
    if (lst [ i ].length() % 2 == 0) n66.push_back(lst [ i ]);

    sort (n66.begin(), n66.end(), greater<string>());
    return n66;
}

int main()
{
    vector < string > lst = {
        "aa",
        "a",
        "aaa"
    };
    vector < string > n66 = sorted_list_sum(lst);
    for (int i = 0; i < n66.size(); i++)
        printf("%s ", n66 [ i ].c_str());
    return 0;
}
